    private void memidFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_memidFocusLost
        // TODO add your handling code here:

        //MEMBER NAME AND GROUP ID!!!!!!!!!!!!
        try
        {
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/mwanzo","Muchiri","Muchiri1234!");

            String name="select * from mem_reg where mem_id = ?";
            PreparedStatement pstmt2= conn.prepareStatement(name);
            pstmt2.setInt(1,Integer.parseInt(memid.getText()));

            ResultSet resultset = pstmt2.executeQuery();

            if(resultset.next()==false)
            {
                JOptionPane.showMessageDialog(null,"Member does not exist or no loan balance");
            }
            else
            {
                do
                {
                    memname.setText(resultset.getString("mem_name"));
                    int x= resultset.getInt("grp_id");
                    grpid.setText(Integer.toString(x));
                }
                while(resultset.next());
            }

        }
        catch(Exception e)
        {
            e.printStackTrace();
        }

        //LOAN AMOUNT BORROWED!!!!!!

        try
        {
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/mwanzo","Muchiri","Muchiri1234!");

            String shares="select*from trans_main where mem_id =? and trans_type=?";
            PreparedStatement pstmt3= conn.prepareStatement(shares);
            pstmt3.setInt(1,Integer.parseInt(memid.getText()));
            pstmt3.setString(2,"LN1");
            ResultSet resultset = pstmt3.executeQuery();

            int z = 0;
            while(resultset.next())
            {

                int x=-(resultset.getInt("trans_amount"));
                z=z+x;
                String y=Integer.toString(z);
                amtborr.setText(y);
            }

        }
        catch(Exception e)
        {
            e.printStackTrace();
        }

        //DATE BORROWED!!!!!!!!!!!!

        try
        {
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/mwanzo","Muchiri","Muchiri1234!");

            String shares="select*from trans_main where mem_id =? and trans_type=?";
            PreparedStatement pstmt4= conn.prepareStatement(shares);
            pstmt4.setInt(1,Integer.parseInt(memid.getText()));
            pstmt4.setString(2,"LN1");
            ResultSet resultset = pstmt4.executeQuery();

            if(resultset.next()==false)
            {
                JOptionPane.showMessageDialog(null,"Member does not exist or no loan balance");
            }
            else
            {
                do
                {
                    Date d3=(resultset.getDate("trans_date"));
                    String d=d3.toString();
                    d1.setText(d);
                }
                while(resultset.next());
            }
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }

    }//GEN-LAST:event_memidFocusLost

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        try
        {
            Connection conn=DriverManager.getConnection("jdbc:mysql://localhost:3306/mwanzo","Muchiri","Muchiri1234!");

            String insert="INSERT INTO trans_main(trans_type,trans_date,mem_id,grp_id,trans_amount)VALUES(?,?,?,?,?)";
            PreparedStatement pstmt= conn.prepareStatement(insert);

            pstmt.setString(1,transtype.getText());

            SimpleDateFormat sdf1=new SimpleDateFormat("dd/MM/yyyy");
            java.util.Date udt=sdf1.parse(d2.getText());
            long ms=udt.getTime();
            java.sql.Date sqdt=new java.sql.Date(ms);
            pstmt.setDate(2,sqdt);

            pstmt.setInt(3,Integer.parseInt(memid.getText()));
            
            pstmt.setInt(4,Integer.parseInt(grpid.getText()));
        
            pstmt.setDouble(5, Double.parseDouble(princpaid.getText()));

            pstmt.executeUpdate();

            //INTEREST!!!!!
            String interest ="INSERT INTO trans_main(trans_type,trans_date,mem_id,grp_id,trans_amount)VALUES(?,?,?,?,?)";
            PreparedStatement pstmt2= conn.prepareStatement(interest);

            pstmt2.setString(1,"INT");//INTEREST

            pstmt2.setDate(2,sqdt);

            pstmt2.setInt(3,Integer.parseInt(memid.getText()));
            
            pstmt2.setInt(4,Integer.parseInt(grpid.getText()));

            pstmt2.setDouble(5,Double.parseDouble(intpaid.getText()));

            pstmt2.executeUpdate();
            JOptionPane.showMessageDialog(null,"Insert successful");

        }

        catch(Exception e)
        {
            e.printStackTrace();
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void amtFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_amtFocusLost
        // TODO add your handling code here:

        //INTEREST AND PRINCIPAL PAID!!!!!!!!!!

        
        int portions=Integer.parseInt(amt.getText());
            double intpaid2= portions*(12.0/112.0);
            double princpaid2= portions*(100.0/112.0);
            
            intpaid.setText(Double.toString(intpaid2));
            princpaid.setText(Double.toString(princpaid2));
        

    }//GEN-LAST:event_amtFocusLost

    private void d2FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_d2FocusLost
        // TODO add your handling code here:

        //INTEREST GAINED!!!!!!

        try
        {

            SimpleDateFormat sdf1=new SimpleDateFormat("dd/MM/yyyy");
            java.util.Date d5=sdf1.parse(d2.getText());

            SimpleDateFormat sdf2=new SimpleDateFormat("yyyy-MM-dd");
            java.util.Date d4=sdf2.parse(d1.getText());

            long diffInMillies = Math.abs(d5.getTime() - d4.getTime());
            long diff = TimeUnit.DAYS.convert(diffInMillies, TimeUnit.MILLISECONDS);

            /*String g=Long.toString(diff);
            JOptionPane.showMessageDialog(null,g);*/
            if(diff<=1460)
            {
                int i=Integer.parseInt(amtborr.getText());
                double rate=0.12;

                double gained= i * rate * (diff/365);
                String g=Double.toString(gained);

                intgained.setText(g);
            }
            else
            {
                JOptionPane.showMessageDialog(null,"Loan Repayment Period Has Exceeded 4 years");
            }

        }
        catch(Exception e)
        {
            e.printStackTrace();
        }

    }//GEN-LAST:event_d2FocusLost

